import React from 'react'
import StatusCard from './statusCard'

const StatusList = ({listID,title, cards}) =>
{
  
    return (
        <div >
            <div>{title}</div>
        <div style={style.listsContainer}> 
       {cards.map(card => (<StatusCard key={card.cardID} title={card.cardTitle} buttons={card.buttons}/> ))} 
       </div>
        </div>
    )
}
const styles = {
    listsContainer:
    {
        display:"flex",
      flexDirection:"row",
    //   backgroundColor : "#dfe3e6",
    //   boredeRadius : 3,
    //   padding :8,
    //   marginRight:8,
    //  height:  "100%",

      
    },
}
export default StatusList;
