import React,{Component} from 'react'
import { connect  } from 'react-redux'
import OperationList from './OperationList'
import OperationAddListButton from './OperationAddListButton'
class MainWindow extends Component
{
     
   
    render()
    {  
        const {lists} = this.props;
        return(
        <div>
        <div class="center">חלון אופרציה</div>
        <div class="right-align" style={styles.lineContainer}>
            <span> שם משימה </span>
            <span> אישור ירידה</span>
            <span> אישור המראה </span>
            <span> המראה </span>
        </div  >
        <div style={styles.listsContainer}>
        {lists.map(list => 
          ( <OperationList listID={list.listID} key ={list.id} title = {list.title} cards = {list.cards}  />))}
          <OperationAddListButton/>
          </div> 
        </div>
        )
    }
}
const mapStateToProps = (state) =>{
    return {
        lists: state.OperationList
    }
}

const styles = {
    listsContainer:
    {
     display:"flex",
      flexDirection:"row", 
      marginBottom: 100,
      padding:3
    },
    lineContainer:
    {   
    
  //  display:"flex",
     width:80,
      flexDirection:"column",
      backgroundColor:"blue", 
      margin: 80,
    },
    
}
export default connect(mapStateToProps)(MainWindow)
