import React , {Component} from 'react'
import TrelloList from "./TrelloList"
import {connect } from 'react-redux'
import TrelloActionButton from './TrelloActionButton'
import {DragDropContext} from "react-beautiful-dnd"
import {sortCountDown} from "../../Actions"
import {refreshCountDown} from '../../Actions'

class MainCountDownComponent extends Component {
  onDragEnd = (result)=>{
    //a function that will run when dragging ends
    const {destination, source, draggableId }= result;
    if (!destination){
      return;
    }
    this.props.dispatch(
      sortCountDown(
        source.droppableId,
        destination.droppableId,
        source.index,
        destination.index,
        draggableId
      )
    );
  };

  componentDidMount() {
    setInterval(() => {
      this.props.dispatch(refreshCountDown())
    }, 1000);
  }

  render ()
  {
    const {lists} = this.props;
    // const {startTimer}=this.props;

    return (
      <DragDropContext   onDragEnd= {this.onDragEnd} > 
      <div>
        <h2>COUNTDOWN WINDOW</h2>
    {/* <div>{startTimer.calendar()}</div> */}
         <div style={lists.styles.listsContainer}>
            {lists.cardsLists.map(list => 
          ( <TrelloList listID={list.id} key ={list.id} title = {list.title} cards = {list.cards}  />))}
          <TrelloActionButton list />

       </div>
      </div>
      </DragDropContext>

    );
  }

}
const styles = {
  listsContainer:
  {
    display:"flex",
    flexDirection:"row",
  }
};
const mapStateToProps = state=> ({
  lists: state.lists,
  // startTimer:state.startTimer



})

export default connect(mapStateToProps) (MainCountDownComponent);
