import React  from 'react'
import  Icon  from '@material-ui/core/Icon';
import Textarea from 'react-textarea-autosize';
import Card from '@material-ui/core/Card';
import  Button  from '@material-ui/core/Button';
import {connect} from "react-redux"
import { addListCountDown, addCardCountDown} from "../../Actions"

class TrelloActionButton extends React.Component {
    state = {
        formOpen: false,
        text: ""
    }
    handleAddList = ()=>{
        const {dispatch} = this.props;
        const {text} = this.state;
        if (text){
            this.setState({
                text:""
            })
            dispatch(addListCountDown(text)); 
        }
        return;
    }
    handleAddCard = ()=>{
        const {dispatch, listID} = this.props;
        const {text} = this.state;
        if (text){
            this.setState({
                text:""
            })
            console.
            dispatch(addCardCountDown(listID , text));
        }
        return;
    }
    renderAddButton=()=>{
        const {list} = this.props;
        const buttonText = list ? "Add another list" : "Add another card";
        const buttonTextOpacity = list ? 1: 0.5;
        const buttonTextColor = list ? "white": "inherit";
        const buttonTextBackground = list ? "rgba(0,0,0,.15)": "inherit";


        return(
              <div 
              onClick = {this.openForm}
              style = {{
                ...styles.openFormButtonGroup,  
                 opacity: buttonTextOpacity ,
                color:buttonTextColor , 
                background:buttonTextBackground}  }>
                  <Icon>add</Icon>
                 <p>{buttonText}</p>
              </div>

        );
    };
    openForm = ()=>{
        this.setState({
            formOpen:true
        });
    };
    closeForm=(e)=>{
        this.setState({
            formOpen:false 
        });
    };

    handleInputChange =e=>{
        this.setState({
           text: e.target.value
        });
    };
    renderForm = ()=> {
        const {list} = this.props;
        const placeholder = list ? "Enter list title" : "Enter title for this card" ;
        const buttonTitle = list ? "Add List" :  "Add Card";


        return <div>
            <Card style ={{
                overflow:"visible",
                minHeight:80,
                minWidth:272,
                padding: "6px 8px 2px",
                
            }}>
            <Textarea 
            placeholder ={placeholder}
            autoFocus
            onBlur={this.closeForm}
            value = {this.state.text}
            onChange = {this.handleInputChange}
            style = {{
                resize:"none",
                width: "100%",
                outline:"none",
                border:"none",
                overflow:"hidden",
            }}
            />
            </Card>
            <div style = {styles.formButtonGroup}> 
                <Button
                onMouseDown = {list ? this.handleAddList : this.handleAddCard }  
                variant="contained" style = {{ color:"white", backgroundColor: "#5aac44"}} >
                    {buttonTitle}{" "}
                </Button> 
                <Icon style = {{marginLeft: 8 , cursor: "pointer" , }} >close </Icon>
            </div>
        </div>;


    }
    render(){
        return this.state.formOpen? this.renderForm(): this.renderAddButton()
    }
}
const styles={
    openFormButtonGroup : {
       display:"flex",
       alignItems: "center", 
       cursor:"pointer",
       borderRadius :3 ,
       height: 36,
       width: 272,
       paddingLeft: 10,
    },
    formButtonGroup:{
        marginTop:8,
        display:"flex",
        alignItems: "center", 
    }
}



export default connect() (TrelloActionButton) ;
