import React,{Component} from 'react'
import { connect  } from 'react-redux'
import OperationList from './OperationList'
import OperationAddListButton from './OperationAddListButton'
import planePic from './plane.jpeg'
import helicopterPic from './helicopter.jpeg'



const picArray=[
    planePic,
    helicopterPic
]
let picIndex=1;
class MainWindow extends Component
{
     
   changePic = (e) =>
   {
      // console.log("IN");
    // e.preventDefault();
    console.log(picIndex);
        e.target.src=picArray[picIndex];
        if(picIndex == 1)
            picIndex=0;
        else
            picIndex++;
       
   }


    render()
    {  
        const {lists} = this.props;
        return(
        <div>
        <div class="center">חלון אופרציה</div>
        <div style={styles.blockStyle}>
        <div style={styles.lineContainer}>
            <p> שם משימה </p>
            <p> אישור ירידה</p>
            <p> אישור המראה </p>
            <p> המראה </p>
        </div  >
        <div style={styles.listsContainer}>
        {lists.map(list => 
          ( <OperationList listID={list.listID} key ={list.id} title = {list.title} cards = {list.cards} changePic={this.changePic} />))}
          <OperationAddListButton/>
          </div> 
        </div>
        </div>
        )
    }
}
const mapStateToProps = (state) =>{
    return {
        lists: state.OperationList
    }
}

const styles = {
    listsContainer:
    {
     //display:"flex",
     display:"inline",
     float:"right",
     backgroundColor:"green", 
     margin:"auto"
  
    },
    lineContainer:
    {   
     //paddingBottom:"3",
     width:"auto",
     float:"right",
    flexDirection:"column",
     backgroundColor:"blue", 
     marginTop:""
     // marginLeft: 450,
      //margin:"auto"
    },
    blockStyle:
    {   
    //display:"inline"
    },
}
export default connect(mapStateToProps)(MainWindow)
