import React from 'react'
import OperationCard from './OperationCard'
import OperationAddCardButton from './OperationAddCardButton'
import planePic from './plane.png'
import helicopterPic from './helicopter.png'

const picArray={
    planePic,
    helicopterPic
}
let picIndex=0;
const  OperationList = ({title , cards , listID}) => {

    this.changePic =(e) =>
    {
        e.target.src=picArray[1];

    }


    return (
        <div style={styles.listsStyle} > 
        <img id={"pic"+listID}src={picArray[0]} style={{width:"50%"}} onClick={this.changePic}></img>
        <form>
        <input type="text" placeholder={title} style={{width:90}}></input>
        </form>
        <div style={styles.listsContainer}>
       {cards.map(card => (<OperationCard key={card.id} title={card.title}/> ))} 
       <OperationAddCardButton listID={listID}/>
       </div>
        </div>
    )
}

const styles = {
    listsContainer:
    {
      display:"flex",

      flexDirection:"row",
      backgroundColor : "#dfe3e6",
      boredeRadius : 3,
      padding :8,
      marginRight:8,
     height:  "100%",

      
    },

    listsStyle:
    {
        float:"right",
        ///float:"right",
        //position: "absolute",
       
    },
}
export default OperationList
