import React, {Component} from 'react';
import {connect } from 'react-redux'
import Popup from "reactjs-popup";
class countDownAddButton extends Component
{

    render()
    {
        console.log(50+this.props.lists.length*140);
        let leftPlace=50+this.props.lists.length*140;       
        return(
  
        <Popup
        trigger={<button style={{position:"absolute", width:"50px", height:"50px"}}className="button">+</button>}
        modal
        closeOnDocumentClick>
       <form>
            <label >
                Mission Title:
                <input type="text" name="title" />
            </label>
            <label style={{rightMargin:"10px"}}>
                Starting Hour
                <input type="time" name="Comments" style={{width:"auto"}} />
            </label>
            <label>
               Ending Hour
                <input type="time" name="Comments" style={{width:"auto"}}/>
            </label>
            <textarea>Extra Comments</textarea>
             <input type="submit" value="Submit" /> 
            
        </form>  
      </Popup>
        )
    }
}
const mapStateToProps = (state)=> ({
    lists: state.CountDownlists.resources,
    events: state.CountDownlists.events
  })

export default connect(mapStateToProps)(countDownAddButton)
