import React from 'react'
import OperationCard from './OperationCard'
import OperationAddCardButton from './OperationAddCardButton'

const  OperationList = ({title , cards , listID}) => {
    return (
        <div style={styles.listsStyle} > 
    
        <span>{title}</span>
        <div style={styles.listsContainer}>
       {cards.map(card => (<OperationCard key={card.id} title={card.title}/> ))} 
       <OperationAddCardButton listID={listID}/>
       </div>
        </div>
    )
}

const styles = {
    listsContainer:
    {
      display:"flex",

      flexDirection:"row",
      backgroundColor : "#dfe3e6",
      boredeRadius : 3,
      padding :8,
      marginRight:8,
     height:  "100%",

      
    },

    listsStyle:
    {
        float:"right",
        ///float:"right",
        //position: "absolute",
       
    },
}
export default OperationList
