import {CONSTANTS} from "../Actions";
import * as moment from 'moment';

let listIDNew=3;
const initialState = {
    title: "This is the title",
    OperationList: [
       {
           listID:0,
           title: 'First List',
           cards:[
               {id:0},
               {id:1}
           ]
       },
       {
        listID:1,
        title: 'Second List',
        cards:[
            {id:0},
            {id:1}
        ]
        }
    ],
    StatusList: [
        {
            listID:0,
            listTitle: 'First List',
            cards:[
                {
                cardTitle: 'First Card',
                cardID:0,
                buttons:[
                        {id:1,titleButton:'1'},
                        {id:2,titleButton:'2'},
                        {id:1,titleButton:'1'},
                        {id:2,titleButton:'2'},
                        {id:1,titleButton:'1'},
                        {id:2,titleButton:'2'},
                    ]
                },
                {
                    cardTitle: 'Second Card',
                    cardID:0,
                    buttons:[
                            {id:1,titleButton:'3'},
                            {id:2,titleButton:'4'},
                        ]
                    }
               ]
         },
         {
         listID:1,
            listTitle: 'Second List',
            cards:[
                {
                cardTitle: 'First Card',
                cardID:0,
                buttons:[
                        {id:1,titleButton:'1'},
                        {id:2,titleButton:'2'},
                    ]
                },
                {
                    cardTitle: 'Second Card',
                    cardID:0,
                    buttons:[
                            {id:1,titleButton:'3'},
                            {id:2,titleButton:'4'},
                        ]
                    }
               ]
         }
     ],
   
    startTimer: moment({hours: 19, minutes: 30}),
    cardsLists: [
    {
        title:  " list 1 ",
        id: 'list-${0}',
        cards: [
        {   id:'cards-${0}',
            text: "we created a static list&card 0 " ,
            duration: moment.duration(53, 'minutes'),
        },
        {   id: 'cards-${1}',
        text: "we created a static list&card 1 ",
        duration: moment.duration(30, 'minutes'),
    },
        ]
    },
    {
        title:  " list 2 ",
        id: 'list-${1}',
        cards: [
            {   id: 'cards-${2}',
                text: "test 1 " ,
                duration: moment.duration(30, 'minutes'),
            },
            {   id: 'cards-${3}',
                text: "test 2 ",
                duration: moment.duration(30, 'minutes'),
            },
            {   id: 'cards-${4}',
                text: "test 3 ",
                duration: moment.duration(30, 'minutes'),
            },
            {   id: 'cards-${5}',
                text: "test 4 ",
                duration: moment.duration(30, 'minutes'),
            },
        ]
    }
   
    ]    
    
    
     
     
}

const OperationReducer = (state = initialState, action) =>{
    let listID=3;
    switch(action.type){
    case CONSTANTS.ADD_LIST_OPERATION:
        const newList = {
            listID : listIDNew, 
            title:"List num"+listIDNew,
            cards: [] , 
        }
        listIDNew++; 
    return {...state,OperationList: [...state.OperationList, newList]};
    
    case CONSTANTS.ADD_CARD_OPERATION:{
        const newCard = {
            id:4
        } 
       
       const newOperationList= state.OperationList.map (list => {
            if(list.listID === action.payload.listID){
                return{
                    ...list,
                    cards:[...list.cards, newCard]
                }
            }else{
                return list;
            }
        });   

        return {...state, OperationList: newOperationList};
        }


    }

    return state;
}

export default OperationReducer;
